@ exercises/practice/rail-fence-cipher/rail-fence-cipher.js starting_line=6 ending_line=7
export const encode = (message, rails) => {
  if (rails < 2) {
    return message;
  }

  const fence = Array(rails).fill().map(() => []);
  let rail = 0;
  let direction = 1;

  for (let i = 0; i < message.length; i++) {
    fence[rail].push(message[i]);

    if (rail === 0) {
      direction = 1;
    } else if (rail === rails - 1) {
      direction = -1;
    }

    rail += direction;
  }

  return fence.flat().join('');
};

