@ exercises/practice/roman-numerals/roman-numerals.js starting_line=6 ending_line=8
export const toRoman = (number) => {
  const romanNumerals = {
    M: 1000,
    CM: 900,
    D: 500,
    CD: 400,
    C: 100,
    XC: 90,
    L: 50,
    XL: 40,
    X: 10,
    IX: 9,
    V: 5,
    IV: 4,
    I: 1,
  };

  let result = '';

  for (const key in romanNumerals) {
    while (number >= romanNumerals[key]) {
      result += key;
      number -= romanNumerals[key];
    }
  }

  return result;
};

