@ exercises/practice/word-count/word-count.js starting_line=6 ending_line=8
export const countWords = (phrase) => {
  const words = phrase.toLowerCase().match(/\b\w+'\w+|\b\w+\b/g);
  const wordCount = {};

  if (words) {
    words.forEach((word) => {
      wordCount[word] = (wordCount[word] || 0) + 1;
    });
  }

  return wordCount;
};
