@ exercises/practice/wordy/wordy.js starting_line=6 ending_line=8
export const answer = (question) => {
  const regex = /^What is (-?\d+)( plus (-?\d+))*(( minus| multiplied by| divided by) (-?\d+))*\?$/;
  const match = question.match(regex);
  if (!match) {
    throw new Error('Invalid question');
  }

  let result = parseInt(match[1]);

  for (let i = 3; i < match.length; i += 4) {
    const operator = match[i];
    const operand = parseInt(match[i + 1]);

    if (operator === ' plus') {
      result += operand;
    } else if (operator === ' minus') {
      result -= operand;
    } else if (operator === ' multiplied by') {
      result *= operand;
    } else if (operator === ' divided by') {
      result /= operand;
    }
  }

  return result;
};

