@ exercises/practice/point-mutations/point-mutations.js starting_line=6 ending_line=9
export class DNA {
  constructor(strand) {
    this.strand = strand;
  }

  hammingDistance(otherStrand) {
    if (this.strand.length !== otherStrand.length) {
      throw new Error('DNA strands must be of equal length');
    }

    let distance = 0;
    for (let i = 0; i < this.strand.length; i++) {
      if (this.strand[i] !== otherStrand[i]) {
        distance++;
      }
    }
    return distance;
  }
}
