@ exercises/practice/tournament/tournament.js starting_line=6 ending_line=8
export const tournamentTally = (input) => {
  const teams = new Map();
  const results = input.split('\n').filter(Boolean);

  results.forEach((result) => {
    const [team1, team2, outcome] = result.split(';');
    if (!teams.has(team1)) {
      teams.set(team1, { MP: 0, W: 0, D: 0, L: 0, P: 0 });
    }
    if (!teams.has(team2)) {
      teams.set(team2, { MP: 0, W: 0, D: 0, L: 0, P: 0 });
    }
    teams.get(team1).MP++;
    teams.get(team2).MP++;

    if (outcome === 'win') {
      teams.get(team1).W++;
      teams.get(team1).P += 3;
      teams.get(team2).L++;
    } else if (outcome === 'draw') {
      teams.get(team1).D++;
      teams.get(team1).P++;
      teams.get(team2).D++;
      teams.get(team2).P++;
    } else if (outcome === 'loss') {
      teams.get(team2).W++;
      teams.get(team2).P += 3;
      teams.get(team1).L++;
    }
  });

  const sortedTeams = Array.from(teams.entries()).sort((a, b) => {
    if (a[1].P === b[1].P) {
      return a[0].localeCompare(b[0]);
    }
    return b[1].P - a[1].P;
  });

  let table = 'Team                           | MP |  W |  D |  L |  P\n';

  sortedTeams.forEach(([team, stats]) => {
    table += `${team.padEnd(30)}|  ${stats.MP} |  ${stats.W} |  ${stats.D} |  ${stats.L} |  ${stats.P}\n`;
  });

  return table;
};

