export const sum = (factors, limit) => {
  const multiples = new Set();
  factors.forEach(factor => {
    for (let i = factor; i < limit; i += factor) {
      multiples.add(i);
    }
  });
  return [...multiples].reduce((sum, num) => sum + num, 0);
};

