export const isPangram = (sentence) => {
  const alphabet = 'abcdefghijklmnopqrstuvwxyz';
  const lowerCaseSentence = sentence.toLowerCase();
  return [...alphabet].every((letter) => lowerCaseSentence.includes(letter));
};

