          }
        } else if (currentNode === this.tail) {
          this.tail = currentNode.prev;
          if (this.tail !== null) {
            this.tail.next = null;
          }
        } else {
          currentNode.prev.next = currentNode.next;
          currentNode.next.prev = currentNode.prev;
        }

        this.length--;
        break;
      }

      currentNode = currentNode.next;
    }
  }

  count() {
    return this.length;
  }
}
