export const encode = (phrase, key) => {
  const alphabet = 'abcdefghijklmnopqrstuvwxyz';
  const m = alphabet.length;
  const [a, b] = key;

  if (gcd(a, m) !== 1) {
    throw new Error('a and m must be coprime.');
  }

  return phrase
    .toLowerCase()
    .replace(/[a-z]/g, (char) => {
      const x = alphabet.indexOf(char);
      const y = (a * x + b) % m;
      return alphabet[y];
    });
};

const gcd = (a, b) => {
