export function triplets({ minFactor, maxFactor, sum }) {
  const triplets = [];

  for (let a = minFactor || 1; a < (maxFactor || sum); a++) {
    for (let b = a + 1; b < (maxFactor || sum); b++) {
      const c = sum - a - b;

      if (c <= b) break;
      if (c * c === a * a + b * b) {
        triplets.push(new Triplet(a, b, c));
      }
    }
  }

  return triplets;
}
