@ exercises/practice/circular-buffer/circular-buffer.js starting_line=6 ending_line=25
class CircularBuffer {
  constructor(size) {
    this.buffer = new Array(size).fill(null);
    this.readIndex = 0;
    this.writeIndex = 0;
    this.isFull = false;
  }

  write(value) {
    if (this.isFull) {
      throw new BufferFullError();
    }
    this.buffer[this.writeIndex] = value;
    this.writeIndex = (this.writeIndex + 1) % this.buffer.length;
    this.isFull = this.readIndex === this.writeIndex;
  }

  read() {
    if (this.isEmpty()) {
      throw new BufferEmptyError();
    }
    const value = this.buffer[this.readIndex];
    this.buffer[this.readIndex] = null;
    this.readIndex = (this.readIndex + 1) % this.buffer.length;
    this.isFull = false;
    return value;
  }

  forceWrite(value) {
    if (this.isFull) {
      this.readIndex = (this.readIndex + 1) % this.buffer.length;
    }
    this.write(value);
  }

  clear() {
    this.buffer.fill(null);
    this.readIndex = 0;
    this.writeIndex = 0;
    this.isFull = false;
  }

  isEmpty() {
    return !this.isFull && this.buffer.every((value) => value === null);
  }
}

export default CircularBuffer;

export class BufferFullError extends Error {
  constructor() {
    super("Buffer is full");
  }
}

export class BufferEmptyError extends Error {
  constructor() {
    super("Buffer is empty");
  }
}
