@ exercises/practice/atbash-cipher/atbash-cipher.js starting_line=6 ending_line=8
export const encode = (text) => {
  const plain = 'abcdefghijklmnopqrstuvwxyz';
  const cipher = 'zyxwvutsrqponmlkjihgfedcba';

  const encodedText = text
    .toLowerCase()
    .replace(/[^a-z0-9]/g, '')
    .split('')
    .map((char) => {
      if (/[a-z]/.test(char)) {
        return cipher[plain.indexOf(char)];
      }
      return char;
    })
    .join('');

  return encodedText
    .match(/.{1,5}/g)
    .join(' ');
};

export const decode = (text) => {
