@ exercises/practice/pascals-triangle/pascals-triangle.js starting_line=6 ending_line=8
export const rows = (numRows) => {
  if (numRows <= 0) {
    return [];
  }

  const triangle = [[1]];

  for (let i = 1; i < numRows; i++) {
    const row = [1];

    for (let j = 1; j < i; j++) {
      row.push(triangle[i - 1][j - 1] + triangle[i - 1][j]);
    }

    row.push(1);
    triangle.push(row);
  }

  return triangle;
};

