@ exercises/practice/square-root/square-root.js starting_line=6 ending_line=8
export const squareRoot = (n) => {
  if (n < 0) {
    throw new Error('Square root of negative numbers is not supported');
  }

  if (n === 0) {
    return 0;
  }

  let x = n;
  let y = 1;

  while (x > y) {
    x = (x + y) / 2;
    y = n / x;
  }

  return Math.floor(x);
};
